package JAVA_BASICS;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 'A';
		switch(a) {
		
		case 'a':
			System.out.print("Lower case");
			break;
			
		case 'A':
			System.out.print("Upper case");
			break;
			
		default :
			break;
			
		}
		
	}

}
